import React, { useState, useEffect } from 'react';
import './adminDashboard.css'; // Custom styles for the dashboard
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [providers, setProviders] = useState([]);
  const [orders, setOrders] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null); // State to hold selected user
  const [selectedProvider, setSelectedProvider] = useState(null); // State to hold selected provider
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Fetch data when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        await fetchUsers();
        await fetchProviders();
        await fetchOrders();
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        setError('Failed to load data');
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Fetch users from the backend
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/users', {
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('token')}` // Pass the JWT token
        }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  // Fetch providers from the backend
  const fetchProviders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/providers', {
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('token')}`
        }
      });
      setProviders(response.data);
    } catch (error) {
      console.error('Error fetching providers:', error);
    }
  };

  // Fetch orders from the backend
  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/orders', {
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('token')}`
        }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  // View User Details
  const handleViewUser = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/admin/users/${userId}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setSelectedUser(response.data); // Set the selected user data in state
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  // View Provider Details
  const handleViewProvider = async (providerId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/admin/providers/${providerId}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setSelectedProvider(response.data); // Set the selected provider data in state
    } catch (error) {
      console.error('Error fetching provider:', error);
    }
  };

  // Delete User
  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await axios.delete(`http://localhost:5000/api/admin/users/${userId}`, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        setUsers(users.filter(user => user._id !== userId)); // Remove user from state
      } catch (error) {
        console.error('Error deleting user:', error);
      }
    }
  };

  // Delete Provider
  const handleDeleteProvider = async (providerId) => {
    if (window.confirm('Are you sure you want to delete this provider?')) {
      try {
        await axios.delete(`http://localhost:5000/api/admin/providers/${providerId}`, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        setProviders(providers.filter(provider => provider._id !== providerId)); // Remove provider from state
      } catch (error) {
        console.error('Error deleting provider:', error);
      }
    }
  };

  // Handle loading and error states
  if (loading) {
    return <div>Loading data...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      {/* Manage Users Section */}
      <section>
        <h2>Manage Users</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td>
                  <button onClick={() => handleViewUser(user._id)}>View</button>
                  <button>Edit</button>
                  <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {selectedUser && (
          <div>
            <h3>User Details</h3>
            <p>Name: {selectedUser.name}</p>
            <p>Email: {selectedUser.email}</p>
            <p>Role: {selectedUser.role}</p>
          </div>
        )}
      </section>

      {/* Manage Providers Section */}
      <section>
        <h2>Manage Providers</h2>
        <table>
          <thead>
            <tr>
              <th>Restaurant Name</th>
              <th>Rating</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {providers.map(provider => (
              <tr key={provider._id}>
                <td>{provider.restaurantName}</td>
                <td>{provider.rating}</td>
                <td>
                  <button onClick={() => handleViewProvider(provider._id)}>View</button>
                  <button>Edit</button>
                  <button onClick={() => handleDeleteProvider(provider._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {selectedProvider && (
          <div>
            <h3>Provider Details</h3>
            <p>Restaurant: {selectedProvider.restaurantName}</p>
            <p>Rating: {selectedProvider.rating}</p>
          </div>
        )}
      </section>

      {/* Manage Orders Section */}
      <section>
        <h2>Manage Orders</h2>
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Provider</th>
              <th>Total Amount</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id}>
                <td>{order._id}</td>
                <td>{order.customerId?.name || 'Unknown Customer'}</td>
                <td>{order.providerId?.restaurantName || 'Unknown Provider'}</td>
                <td>${order.totalAmount}</td>
                <td>{order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
